package abstraction;

public class BasicCalci extends AbstractBasicCalci{

	@Override
	int add(int a, int b) {
		// TODO Auto-generated method stub
		return a+b;
	}

	@Override
	int sub(int a, int b) {
		// TODO Auto-generated method stub
		return a-b;
	}

	@Override
	int mul(int a, int b) {
		// TODO Auto-generated method stub
		return a*b;
	}

}
